var context = typeof module == "object" ? module.exports : this;
context.DefaultLogger = MainLogger.named("DefaultLogger");

for(var y in context.DefaultLogger) {
  if(typeof context.DefaultLogger[y] == 'function') {
    context[y] = function() {
			var member = y;
			return function(){		
				context.DefaultLogger[member].apply(context.DefaultLogger, arguments);
			}
    }();
  }
}

for(var y in MainLogger) {
  context[y] = MainLogger[y];
}

context.Logger = Logger;
context.Levels = Levels;
context.DefaultLoggerImpl = DefaultLoggerImpl;
