
var TiLoggerImpl = (function () {
    TiLoggerImpl = function () {
        this.initialize();
    }
    var pp = TiLoggerImpl.prototype;
    pp.initialize = function () {};
    pp.loggerimplname = "yajas";
    pp.log = function(what, lvl, obj, logger) {
        	var lvlname = logger.levels.getName(lvl);
			lvlname = (lvlname === null ? 'info' : lvlname);
			var logfnct = null;
			if(Ti.API[lvlname] != undefined) {
				logfnct = Ti.API[lvlname];
			} else {
				logfnct = Ti.API.info;
			}
			if(obj != null) {
				logfnct('--------------')
			}
			logfnct(logger.name + ' :: ' + logger.levels.getName(lvl) + ' : ' + what);
			if(obj != null) {
				logfnct(JSON.stringify(obj));
				logfnct('--^--^--^--^--')
			}
    };
    return TiLoggerImpl;
})();


