var logger;

/* Tests for default logger */
logger = require("../dist/logger4js.js");

logger.debug("browserify 1");
logger.info("browserify 2");
logger.trace("browserify 3");
logger.warning("browserify 4");
logger.error("browserify 5");

/* Tests for custom logger */
logger = require("../dist/logger4js.js").named("custom");

logger.debug("browserify 1");
logger.info("browserify 2");
logger.trace("browserify 3");
logger.warning("browserify 4");
logger.error("browserify 5");

/* Tests for noprint */
logger = require("../dist/logger4js.js").named("mute");
require("../dist/logger4js.js").noprint();

/* The following logs should not appear on your browser */
logger.debug("browserify 1");
logger.info("browserify 2");
logger.trace("browserify 3");
logger.warning("browserify 4");
logger.error("browserify 5");
